INSERT INTO `wp_poems` VALUES ('701', '大风歌', null, '刘邦', null, null, '大风起兮云飞扬，威加海内兮归故乡，安得猛士兮守四方！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
