INSERT INTO `wp_poems` VALUES ('704', '十五从军征', null, '', null, null, '十五从军征，八十始得归。道逢乡里人：“家中有阿谁？”“遥看是君家，松柏冢累累。”(遥看 一作：遥望)兔从狗窦入，雉从梁上飞。中庭生旅谷，井上生旅葵。舂谷持作饭，采葵持作羹。羹饭一时熟，不知贻阿谁？出门东向看，泪落沾我衣。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('705', '明月何皎皎', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('706', '江南', null, '', null, null, '江南可采莲，莲叶何田田，鱼戏莲叶间。鱼戏莲叶东，鱼戏莲叶西，鱼戏莲叶南，鱼戏莲叶北。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('707', '相鼠', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('708', '东城高且长', null, '', null, null, '东城高且长，逶迤自相属。回风动地起，秋草萋已绿。四时更变化，岁暮一何速！晨风怀苦心，蟋蟀伤局促。荡涤放情志，何为自结束？燕赵多佳人，美者颜如玉。被服罗裳衣，当户理清曲。音响一何悲！弦急知柱促。驰情整巾带，沉吟聊踯躅。思为双飞燕，衔泥巢君屋。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('709', '赠秀才入军·其十四', null, '嵇康', null, null, '息徒兰圃，秣马华山。流磻平皋，垂纶长川。目送归鸿，手挥五弦。俯仰自得，游心太玄。嘉彼钓翁，得鱼忘筌。郢人逝矣，谁与尽言？', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('710', '迢迢牵牛星', null, '', null, null, '迢迢牵牛星，皎皎河汉女。纤纤擢素手，札札弄机杼。终日不成章，泣涕零如雨；河汉清且浅，相去复几许！盈盈一水间，脉脉不得语。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('711', '桓灵时童谣', null, '', null, null, '举秀才，不知书。举孝廉，父别居。寒素清白浊如泥，高第良将怯如鸡。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('712', '杂诗七首·其四', null, '曹植', null, null, '南国有佳人，容华若桃李。朝游江北岸，夕宿潇湘沚。时俗薄朱颜，谁为发皓齿？俯仰岁将暮，荣耀难久恃。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('713', '上邪', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('714', '回车驾言迈', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('715', '伐檀', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('716', '古歌', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('717', '室思', null, '徐干', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('718', '涉江采芙蓉', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('719', '客从远方来', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('720', '行行重行行', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('721', '思吴江歌', null, '张翰', null, null, '秋风起兮木叶飞，吴江水兮鲈正肥。三千里兮家未归，恨难禁兮仰天悲。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('722', '拟行路难·其四', null, '鲍照', null, null, '泻水置平地，各自东西南北流。人生亦有命，安能行叹复坐愁？酌酒以自宽，举杯断绝歌路难。心非木石岂无感？吞声踯躅不敢言。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('723', '诏问山中何所有赋诗以答', null, '陶弘景', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('724', '吴孙皓初童谣', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('725', '杂诗', null, '陶渊明', null, null, '人生无根蒂，飘如陌上尘。分散逐风转，此已非常身。落地为兄弟，何必骨肉亲！得欢当作乐，斗酒聚比邻。盛年不重来，一日难再晨。及时当勉励，岁月不待人。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('726', '夜夜曲', null, '沈约', null, null, '河汉纵且横，北斗横复直。星汉空如此，宁知心有忆？孤灯暧不明，寒机晓犹织。零泪向谁道，鸡鸣徒叹息。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('727', '赠范晔诗', null, '陆凯', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('728', '赠从弟', null, '刘桢', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('729', '凛凛岁云暮', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('730', '七哀诗三首·其一', null, '王粲', null, null, '西京乱无象，豺虎方遘患。复弃中国去，委身适荆蛮。亲戚对我悲，朋友相追攀。出门无所见，白骨蔽平原。路有饥妇人，抱子弃草间。顾闻号泣声，挥涕独不还。“未知身死处，何能两相完？”驱马弃之去，不忍听此言。南登霸陵岸，回首望长安，悟彼下泉人，喟然伤心肝。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('731', '咏史·郁郁涧底松', null, '左思', null, null, '郁郁涧底松，离离山上苗。以彼径寸茎，荫此百尺条。世胄蹑高位，英俊沉下僚。地势使之然，由来非一朝。金张藉旧业，七叶珥汉貂。冯公岂不伟，白首不见招。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('732', '陇头歌辞三首', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('733', '蜀道难·其二', null, '萧纲', null, null, '巫山七百里，巴水三回曲。笛声下复高，猿啼断还续。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('734', '边城思', null, '何逊', null, null, '柳黄未吐叶，水绿半含苔。春色边城动，客思故乡来。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('735', '梅花落', null, '鲍照', null, null, '中庭多杂树，偏为梅咨嗟。问君何独然？念其霜中能作花，露中能作实。摇荡春风媚春日，念尔零落逐风飚，徒有霜华无霜质。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('736', '归园田居·其三', null, '陶渊明', null, null, '种豆南山下，草盛豆苗稀。晨兴理荒秽，带月荷锄归。道狭草木长，夕露沾我衣。衣沾不足惜，但使愿无违。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('737', '人日思归', null, '薛道衡', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('738', '估客乐四首', null, '释宝月', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('739', '作蚕丝', null, '', null, null, '春蚕不应老，昼夜常怀丝。何惜微躯尽，缠绵自有时。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('740', '敕勒歌', null, '', null, null, '敕勒川，阴山下。天似穹庐，笼盖四野。天苍苍，野茫茫。风吹草低见牛羊。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('741', '断句', null, '刘昶', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('742', '山中杂诗', null, '吴均', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('743', '晚出新亭', null, '阴铿', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('744', '晚登三山还望京邑', null, '谢朓', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('745', '入若耶溪', null, '王籍', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('746', '侍宴咏石榴', null, '孔绍安', null, null, '可惜庭中树，移根逐汉臣。只为来时晚，花开不及春。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('747', '寄王琳', null, '庾信', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('748', '蜀道难·其一', null, '萧纲', null, null, '建平督邮道，鱼复永安宫。若奏巴渝曲，时当君思中。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('749', '酌贪泉', null, '吴隐之', null, null, '古人云此水，一歃怀千金。试使夷齐饮，终当不易心。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('750', '宿甘露寺僧舍', null, '曾公亮', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('751', '晚泊岳阳', null, '欧阳修', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('752', '柳', null, '寇准', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('753', '塞上', null, '柳开', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('754', '咏傀儡', null, '杨亿', null, null, '鲍老当筵笑郭郎，笑他舞袖太郎当。若教鲍老当筵舞，转更郎当舞袖长。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('755', '于长安归还扬州九月九日行薇山亭赋韵 / 长安九日诗', null, '江总', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('756', '春居杂兴·两株桃杏映篱斜', null, '王禹偁', null, null, '两株桃杏映篱斜，妆点商州副使家。何事春风容不得？和莺吹折数枝花。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('757', '初晴游沧浪亭', null, '苏舜钦', null, null, '夜雨连明春水生，娇云浓暖弄阴晴。帘虚日薄花竹静，时有乳鸠相对鸣。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('758', '折杨柳歌辞五首', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('759', '陶者', null, '梅尧臣', null, null, '陶尽门前土，屋上无片瓦。十指不沾泥，鳞鳞居大厦。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('760', '落花', null, '宋祁', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('761', '之零陵郡次新亭', null, '范云', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('762', '柳枝词', null, '郑文宝', null, null, '亭亭画舸系春潭，直到行人酒半酣。不管烟波与风雨，载将离恨过江南。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('763', '蚕妇', null, '张俞', null, null, '昨日入城市，归来泪满巾。遍身罗绮者，不是养蚕人。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('764', '王孙游', null, '谢朓', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('765', '丰乐亭游春·其三', null, '欧阳修', null, null, '红树青山日欲斜，长郊草色绿无涯。游人不管春将老，来往亭前踏落花。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('766', '登飞来峰', null, '王安石', null, null, '飞来山上千寻塔，闻说鸡鸣见日升。(飞来山 一作：飞来峰)不畏浮云遮望眼，只缘身在最高层。(只缘 一作：自缘)', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('767', '考试毕登铨楼', null, '梅尧臣', null, null, '春云浓淡日微光，双阙重门耸建章。不上楼来知几日，满城无算柳梢黄。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('768', '画眉鸟', null, '欧阳修', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('769', '书河上亭壁', null, '寇准', null, null, '岸阔樯稀波渺茫，独凭危槛思何长。萧萧远树疏林外，一半秋山带夕阳。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('770', '别滁', null, '欧阳修', null, null, '花光浓烂柳轻明，酌酒花前送我行。我亦且如常日醉，莫教弦管作离声。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('771', '叠题乌江亭', null, '王安石', null, null, '百战疲劳壮士哀，中原一败势难回。江东子弟今虽在，肯与君王卷土来？', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('772', '咏柳', null, '曾巩', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('773', '乡思', null, '李觏', null, null, '人言落日是天涯，望极天涯不见家。已恨碧山相阻隔，碧山还被暮云遮。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('774', '城南', null, '曾巩', null, null, '雨过横塘水满堤，乱山高下路东西。一番桃李花开尽，惟有青青草色齐。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('775', '自责二首', null, '朱淑真', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('776', '春日', null, '张耒', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('777', '春日', null, '晁冲之', null, null, '阴阴溪曲绿交加，小雨翻萍上浅沙。鹅鸭不知春去尽，争随流水趁桃花。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('778', '还自广陵', null, '秦观', null, null, '天寒水鸟自相依，十百为群戏落晖。过尽行人都不起，忽闻水响一齐飞。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('779', '三江小渡', null, '杨万里', null, null, '溪水将桥不复回，小舟犹倚短篙开。交情得似山溪渡，不管风波去又来。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('780', '剑门道中遇微雨', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('781', '除放自石湖归苕溪', null, '姜夔', null, null, '美人台上昔欢娱，今日空台望五湖。残雪未融青草死，苦无麋鹿过姑苏。黄帽传呼睡不成，投稿细细激流冰。分明旧泊江南岸，舟尾春风飐客灯。千门列炬散林鸦，儿女相思未到家。应是不眠非守岁，小客春意入灯花。三生定自陆天随，又向顺松作客归。已拚新年舟上过，倩人和雪洗片衣。沙尾风回一棹寒，椒花今夕不登盘。百年草草都寒夜，自琢春词剪烛看。笠泽茫茫雁影微，玉峰重叠护云衣。长桥寂寞春寒放，只有诗人一舸归。桑间篝火却定蚕，风土相传我未谙。但得明年少行役，只裁白伫作春衫。少小知名翰墨场，十年心事只凄凉。旧时曾作梅花赋，研墨于今亦自香。环玦随波冷未销，古苔留雪卧墙腰。谁家玉笛吹春怨，看见鹅黄上柳条。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('782', '初入淮河四绝句·其三', null, '杨万里', null, null, '两岸舟船各背驰，波痕交涉亦难为。只余鸥鹭无拘管，北去南来自在飞。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('783', '读山海经·其十', null, '陶渊明', null, null, '精卫衔微木，将以填沧海。刑天舞干戚，猛志固常在。同物既无虑，化去不复悔。徒设在昔心，良辰讵可待。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('784', '山园小梅·其一', null, '林逋', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('785', '鲁山山行', null, '梅尧臣', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('786', '戏答元珍', null, '欧阳修', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('787', '东溪', null, '梅尧臣', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('788', '夏意', null, '苏舜钦', null, null, '别院深深夏席清，石榴开遍透帘明。(夏席清 一作：夏簟清)树阴满地日当午，梦觉流莺时一声。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('789', '元日', null, '王安石', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('790', '和子由渑池怀旧', null, '苏轼', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('791', '题西林壁', null, '苏轼', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('792', '禾熟', null, '孔平仲', null, null, '百里西风禾黍香，鸣泉落窦谷登场。老牛粗了耕耘债，啮草坡头卧夕阳。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('793', '苏秀道中', null, '曾几', null, null, '苏秀道中，自七月二十五日夜大雨三日，秋苗以苏，喜而有作。一夕骄阳转作霖，梦回凉冷润衣襟。不愁屋漏床床湿，且喜溪流岸岸深。千里稻花应秀色，五更桐叶最佳音。无田似我犹欣舞，何况田间望岁心。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('794', '春日', null, '汪藻', null, null, '一春略无十日晴，处处浮云将雨行。野田春水碧于镜，人影渡傍鸥不惊。桃花嫣然出篱笑，似开未开最有情。茅茨烟暝客衣湿，破梦午鸡啼一声。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('795', '垓下歌', null, '项羽', null, null, '力拔山兮气盖世。时不利兮骓不逝。骓不逝兮可奈何！虞兮虞兮奈若何！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('796', '落梅', null, '刘克庄', null, null, '一片能教一断肠，可堪平砌更堆墙。飘如迁客来过岭，坠似骚人去赴湘。乱点莓苔多莫数，偶粘衣袖久犹香。东风谬掌花权柄，却忌孤高不主张。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('797', '题临安邸', null, '林升', null, null, '山外青山楼外楼，西湖歌舞几时休？暖风熏得游人醉，直把杭州作汴州。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('798', '舟过安仁', null, '杨万里', null, null, '一叶渔船两小童，收篙停棹坐船中。怪生无雨都张伞，不是遮头是使风。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('799', '秋风辞', null, '刘彻', null, null, '秋风起兮白云飞，草木黄落兮雁南归。兰有秀兮菊有芳，怀佳人兮不能忘。泛楼船兮济汾河，横中流兮扬素波。箫鼓鸣兮发棹歌，欢乐极兮哀情多。少壮几时兮奈老何！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('800', '约客 / 有约', null, '赵师秀', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('801', '月儿弯弯照九州', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('802', '白马篇', null, '曹植', null, null, '白马饰金羁，连翩西北驰。借问谁家子，幽并游侠儿。 少小去乡邑，扬声沙漠垂。宿昔秉良弓，楛矢何参差。控弦破左的，右发摧月支。仰手接飞猱，俯身散马蹄。狡捷过猴猿，勇剽若豹螭。边城多警急，虏骑数迁移。羽檄从北来，厉马登高堤。长驱蹈匈奴，左顾凌鲜卑。弃身锋刃端，性命安可怀？父母且不顾，何言子与妻！名编壮士籍，不得中顾私。捐躯赴国难，视死忽如归！(名编 一作：名在)', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('803', '新凉', null, '徐玑', null, null, '水满田畴稻叶齐，日光穿树晓烟低。黄莺也爱新凉好，飞过青山影里啼。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('804', '绝句·古木阴中系短篷', null, '志南', null, null, '古木阴中系短篷，杖藜扶我过桥东。沾衣欲湿杏花雨，吹面不寒杨柳风。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('805', '湖上', null, '徐元杰', null, null, '花开红树乱莺啼，草长平湖白鹭飞。风日晴和人意好，夕阳箫鼓几船归。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('806', '同儿辈赋未开海棠', null, '元好问', null, null, '枝间新绿一重重， 小蕾深藏数点红。爱惜芳心莫轻吐， 且教桃李闹春风。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('807', '寒菊 / 画菊', null, '郑思肖', null, null, '花开不并百花丛，独立疏篱趣未穷。宁可枝头抱香死，何曾吹落北风中。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('808', '京师得家书', null, '袁凯', null, null, '江水三千里，家书十五行。行行无别语，只道早还乡。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('809', '燕歌行二首·其一', null, '曹丕', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('810', '言志', null, '唐寅', null, null, '不炼金丹不坐禅，不为商贾不耕田。闲来写就青山卖，不使人间造孽钱。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('811', '临平泊舟', null, '黄庚', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('812', '咏怀八十二首·其一', null, '阮籍', null, null, '夜中不能寐，起坐弹鸣琴。薄帷鉴明月，清风吹我襟。孤鸿号外野，翔鸟鸣北林。徘徊将何见？忧思独伤心。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('813', '登江中孤屿', null, '谢灵运', null, null, '江南倦历览，江北旷周旋。怀新道转迥，寻异景不延。乱流趋正绝，孤屿媚中川。云日相晖映，空水共澄鲜。表灵物莫赏，蕴真谁为传。想象昆山姿，缅邈区中缘。始信安期术，得尽养生年。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('814', '饮酒·其五', null, '陶渊明', null, null, '结庐在人境，而无车马喧。问君何能尔？心远地自偏。采菊东篱下，悠然见南山。山气日夕佳，飞鸟相与还。此中有真意，欲辨已忘言。(辨 通：辩)', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('815', '龟虽寿 / 神龟虽寿', null, '曹操', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('816', '即事', null, '夏完淳', null, null, '复楚情何极，亡秦气未平。雄风清角劲，落日大旗明。缟素酬家国，戈船决死生！胡笳千古恨，一片月临城。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('817', '真州绝句', null, '王士祯', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('818', '出居庸关', null, '朱彝尊', null, null, '居庸关上子规啼，饮马流泉落日低。雨雪自飞千嶂外，榆林只隔数峰西。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('819', '关山月', null, '徐陵', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('820', '萤火', null, '赵执信', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('821', '岁暮到家/岁末到家', null, '蒋士铨', null, null, '爱子心无尽，归家喜及辰。寒衣针线密，家信墨痕新。见面怜清瘦，呼儿问苦辛。低徊愧人子，不敢叹风尘。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('822', '马嵬', null, '袁枚', null, null, '莫唱当年长恨歌，人间亦自有银河。石壕村里夫妻别，泪比长生殿上多。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('823', '秣陵怀古', null, '纳兰性德', null, null, '山色江声共寂寥，十三陵树晚萧萧。中原事业如江左，芳草何须怨六朝。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('824', '对酒', null, '秋瑾', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('825', '画', null, '王维', null, null, '远看山有色，近听水无声。春去花还在，人来鸟不惊。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('826', '所见', null, '袁枚', null, null, '牧童骑黄牛，歌声振林樾。意欲捕鸣蝉，忽然闭口立。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('827', '咏史', null, '龚自珍', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('828', '读陆放翁集', null, '梁启超', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('829', '村居', null, '高鼎', null, null, '草长莺飞二月天，拂堤杨柳醉春烟。儿童散学归来早，忙趁东风放纸鸢。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('830', '春愁', null, '丘逢甲', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('831', '山行杂咏', null, '袁枚', null, null, '十里崎岖半里平，一峰才送一峰迎。青山似茧将人裹，不信前头有路行。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('832', '竹石', null, '郑燮', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('833', '狱中题壁', null, '谭嗣同', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('834', '泊船瓜洲', null, '王安石', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('835', '书愤五首·其一', null, '陆游', null, null, '早岁那知世事艰，中原北望气如山。楼船夜雪瓜洲渡，铁马秋风大散关。塞上长城空自许，镜中衰鬓已先斑。出师一表真名世，千载谁堪伯仲间！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('836', '秋夜将晓出篱门迎凉有感二首', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('837', '十一月四日风雨大作二首', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('838', '乡村四月', null, '翁卷', null, null, '绿遍山原白满川，子规声里雨如烟。乡村四月闲人少，才了蚕桑又插田。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('839', '东城', null, '赵孟頫', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('840', '台山杂咏', null, '元好问', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('841', '二砺', null, '郑思肖', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('842', '过零丁洋', null, '文天祥', null, null, '辛苦遭逢起一经，干戈寥落四周星。山河破碎风飘絮，身世浮沉雨打萍。惶恐滩头说惶恐，零丁洋里叹零丁。人生自古谁无死，留取丹心照汗青。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('843', '上京即事', null, '萨都剌', null, null, '牛羊散漫落日下，野草生香乳酪甜。卷地朔风沙似雪，家家行帐下毡帘。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('844', '发淮安', null, '杨士奇', null, null, '岸蓼疏红水荇青，茨菰花白小如蓱。双鬟短袖惭人见，背立船头自采菱。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('845', '石灰吟', null, '于谦', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('846', '就义诗', null, '杨继盛', null, null, '浩气还太虚，丹心照千古。生平未报国，留作忠魂补。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('847', '自湘东驿遵陆至芦溪', null, '查慎行', null, null, '黄花古渡接芦溪，行过萍乡路渐低。吠犬鸣鸡村远近，乳鹅新鸭岸东西。丝缫细雨沾衣润，刀剪良苗出水齐。犹与湖南风土近，春深无处不耕犁。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('848', '闯王', null, '', null, null, '朝求升，暮求合，近来贫汉难存活。早早开门拜闯王，管教大小都欢悦。杀牛羊，备酒浆，开了城门迎闯王，闯王来了不纳粮。吃他娘，着她娘，吃着不够有闯王。不当差，不纳粮，大家快活过一场。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('849', '乡人至夜话', null, '李昌祺', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('850', '题《墨葡萄图》 / 题葡萄图', null, '徐渭', null, null, '半生落魄已成翁,独立书斋啸晚风。笔底明珠无处卖,闲抛闲掷野藤中。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('851', '枕石', null, '高攀龙', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('852', '闻鹧鸪', null, '尤侗', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('853', '真州绝句', null, '王士祯', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('854', '感旧四首', null, '黄景仁', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('855', '狱中赠邹容', null, '章炳麟', null, null, '邹容吾小弟，被发下瀛州。快剪刀除辫，干牛肉作餱。英雄一入狱，天地���悲秋。临命须掺手，乾坤只两头！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('856', '遣兴', null, '袁枚', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('857', '病牛', null, '李纲', null, null, '耕犁千亩实千箱，力尽筋疲谁复伤？但得众生皆得饱，不辞羸病卧残阳。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('858', '咏牡丹', null, '陈与义', null, null, '一自胡尘入汉关，十年伊洛路漫漫。青墩溪畔龙钟客，独立东风看牡丹。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('859', '临安春雨初霁', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('860', '院中独坐', null, '虞集', null, null, '何处它年寄此生，山中江上总关情。无端绕屋长松树，尽把风声作雨声。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('861', '塞上曲送元美', null, '李攀龙', null, null, '白羽如霜出塞寒，胡烽不断接长安。城头一片西山月，多少征人马上看。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('862', '步出夏门行·观沧海', null, '曹操', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('863', '暑旱苦热', null, '王令', null, null, '清风无力屠得热，落日着翅飞上山。人固已惧江海竭，天岂不惜河汉干？昆仑之高有积雪，蓬莱之远常遗寒。不能手提天下往，何忍身去游其间？', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('864', '村居', null, '张舜民', null, null, '水绕陂田竹绕篱，榆钱落尽槿花稀。夕阳牛背无人卧，带得寒鸦两两归。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('865', '归园田居·其一', null, '陶渊明', null, null, '少无适俗韵，性本爱丘山。误落尘网中，一去三十年。(误落 一作：误入)羁鸟恋旧林，池鱼思故渊。开荒南野际，守拙归园田。方宅十余亩，草屋八九间。榆柳荫后檐，桃李罗堂前。暧暧远人村，依依墟里烟。狗吠深巷中，鸡鸣桑树颠。(颠 通 巅)户庭无尘杂，虚室有余闲。久在樊笼里，复得返自然。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('866', '别云间', null, '夏完淳', null, null, '三年羁旅客，今日又南冠。无限河山泪，谁言天地宽！(河山 一作：山河)已知泉路近，欲别故乡难。毅魄归来日，灵旗空际看。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('867', '出郊', null, '杨慎', null, null, '高田如楼梯，平田如棋局。白鹭忽飞来，点破秧针绿。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('868', '咏荆轲', null, '陶渊明', null, null, '燕丹善养士，志在报强嬴。招集百夫良，岁暮得荆卿。君子死知己，提剑出燕京；素骥鸣广陌，慷慨送我行。雄发指危冠，猛气冲长缨。饮饯易水上，四座列群英。渐离击悲筑，宋意唱高声。萧萧哀风逝，淡淡寒波生。商音更流涕，羽奏壮士惊。心知去不归，且有后世名。登车何时顾，飞盖入秦庭。凌厉越万里，逶迤过千城。图穷事自至，豪主正怔营。惜哉剑术疏，奇功遂不成。其人虽已没，千载有馀情。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('869', '己亥杂诗·其二百二十', null, '龚自珍', null, null, '九州生气恃风雷，万马齐喑究可哀。我劝天公重抖擞，不拘一格降人才。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('870', '感事', null, '袁宏道', null, null, '湘山晴色远微微，尽日江头独醉归。不见两关传露布，尚闻三殿未垂衣。边筹自古无中下，朝论于今有是非。日暮平沙秋草乱，一双白鸟避人飞。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('871', '饮湖上初晴后雨二首·其二', null, '苏轼', null, null, '水光潋滟晴方好，山色空蒙雨亦奇。(蒙 通：濛)欲把西湖比西子，淡妆浓抹总相宜。(淡妆浓抹 一作：浓妆淡抹)', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('872', '雨中登岳阳楼望君山', null, '黄庭坚', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('873', '蒿里行', null, '曹操', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('874', '蒹葭', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('875', '咏早梅 / 扬州法曹梅花盛开', null, '何逊', null, null, '兔园标物序，惊时最是梅。衔霜当路发，映雪拟寒开。枝横却月观，花绕凌风台。朝洒长门泣，夕驻临邛杯。应知早飘落，故逐上春来。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('876', '拟行路难·其六', null, '鲍照', null, null, '对案不能食，拔剑击柱长叹息。丈夫生世会几时？安能蹀躞垂羽翼！弃置罢官去，还家自休息。朝出与亲辞，暮还在亲侧。弄儿床前戏，看妇机中织。自古圣贤尽贫贱，何况我辈孤且直！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('877', '病起书怀', null, '陆游', null, null, '病骨支离纱帽宽，孤臣万里客江干。位卑未敢忘忧国，事定犹须待阖棺。天地神灵扶庙社，京华父老望和銮。出师一表通今古，夜半挑灯更细看。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('878', '登快阁', null, '黄庭坚', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('879', '博浪沙', null, '陈孚', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('880', '夜过借园见主人坐月下吹笛', null, '袁枚', null, null, '秋夜访秋士，先闻水上音。半天凉月色，一笛酒人心。响遏碧云近，香传红藕深。相逢清露下，流影湿衣襟。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('881', '别老母', null, '黄景仁', null, null, '搴帷拜母河梁去，白发愁看泪眼枯。惨惨柴门风雪夜，此时有子不如无。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('882', '惠崇春江晚景二首 / 惠崇春江晓景二首', null, '苏轼', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('883', '寻胡隐君', null, '高启', null, null, '渡水复渡水，看花还看花。春风江上路，不觉到君家。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('884', '春暮西园', null, '高启', null, null, '绿池芳草满晴波，春色都从雨里过。知是人家花落尽，菜畦今日蝶来多。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('885', '幼女词', null, '毛铉', null, null, '下床着新衣，初学小姑拜。低头羞见人，双手结裙带。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('886', '春雁', null, '王恭', null, null, '春风一夜到衡阳，楚水燕山万里长。莫道春来便归去，江南虽好是他乡。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('887', '山家', null, '刘因', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('888', '马上作', null, '戚继光', null, null, '南北驱驰报主情，江花边草笑平生。(边草 一作：边月)一年三百六十日，多是横戈马上行。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('889', '绝句', null, '王庭筠', null, null, '竹影和诗瘦，梅花入梦香。可怜今夜月，不肯下西厢。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('890', '柯敬仲墨竹', null, '李东阳', null, null, '莫将画竹论难易，刚道繁难简更难。君看萧萧只数叶，满堂风雨不胜寒。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('891', '舟夜书所见', null, '查慎行', null, null, '月黑见渔灯，孤光一点萤。微微风簇浪，散作满河星。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('892', '题画', null, '沈周', null, null, '碧水丹山映杖藜，夕阳犹在小桥西。微吟不道惊溪鸟，飞入乱云深处啼。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('893', '塞下曲', null, '谢榛', null, null, '暝色满西山，将军猎骑还。隔河见烽火，骄虏夜临关。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('894', '重赠吴国宾', null, '边贡', null, null, '汉江明月照归人，万里秋风一叶身。休把客衣轻浣濯，此中犹有帝京尘。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('895', '富春至严陵山水甚佳', null, '纪昀', null, null, '浓似春云淡似烟，参差绿到大江边。斜阳流水推篷坐，翠色随人欲上船。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('896', '赠刘景文 / 冬景', null, '苏轼', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('897', '梅花 / 梅', null, '王安石', null, null, '墙角数枝梅，凌寒独自开。遥知不是雪，为有暗香来。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('898', '金错刀行', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('899', '州桥', null, '范成大', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('900', '晓出净慈寺送林子方', null, '杨万里', null, null, '毕竟西湖六月中，风光不与四时同。接天莲叶无穷碧，映日荷花别样红。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('901', '小池', null, '杨万里', null, null, '泉眼无声惜细流，树阴照水爱晴柔。小荷才露尖尖角，早有蜻蜓立上头。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('902', '墨梅', null, '王冕', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('903', '己亥杂诗·其五', null, '龚自珍', null, null, '浩荡离愁白日斜，吟鞭东指即天涯。落红不是无情物，化作春泥更护花。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('904', '论诗五首·其二', null, '赵翼', null, null, '李杜诗篇万口传，至今已觉不新鲜。江山代有才人出，各领风骚数百年。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('905', '雨后池上', null, '刘攽', null, null, '一雨池塘水面平，淡磨明镜照檐楹。东风忽起垂杨舞，更作荷心万点声。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('906', '春夜', null, '王安石', null, null, '金炉香烬漏声残，剪剪轻风阵阵寒。春色恼人眠不得，月移花影上栏杆。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('907', '春日偶成', null, '程颢', null, null, '云淡风轻近午天，傍花随柳过前川。时人不识余心乐，将谓偷闲学少年。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('908', '海棠', null, '苏轼', null, null, '东风袅袅泛崇光，香雾空蒙月转廊。只恐夜深花睡去，故烧高烛照红妆。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('909', '三衢道中', null, '曾几', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('910', '落花', null, '朱淑真', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('911', '夏日绝句', null, '李清照', null, null, '生当作人杰，死亦为鬼雄。至今思项羽，不肯过江东。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('912', '春游湖', null, '徐俯', null, null, '双飞燕子几时回？夹岸桃花蘸水开。春雨断桥人不度，小舟撑出柳阴来。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('913', '襄邑道中', null, '陈与义', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('914', '示儿', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('915', '汴京纪事', null, '刘子翚', null, null, '辇毂繁华事可伤，师师垂老过湖湘。缕衣檀板无颜色，一曲当时动帝王。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('916', '春怀示邻里', null, '陈师道', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('917', '临平道中', null, '道潜', null, null, '风蒲猎猎弄清柔，欲立蜻蜓不自由。五月临平山下路，藕花无数满汀州。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('918', '示三子', null, '陈师道', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('919', '夏日田园杂兴·其七', null, '范成大', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('920', '梅花绝句·其一', null, '陆游', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('921', '淮村兵后', null, '戴复古', null, null, '小桃无主自开花，烟草茫茫带晚鸦。几处败垣围故井，向来一一是人家。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('922', '春日', null, '朱熹', null, null, '胜日寻芳泗水滨，无边光景一时新。等闲识得东风面，万紫千红总是春。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('923', '横塘', null, '范成大', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('924', '游园不值', null, '叶绍翁', null, null, '应怜屐齿印苍苔，小扣柴扉久不开。春色满园关不住，一枝红杏出墙来。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('925', '荆轲歌 / 渡易水歌', null, '', null, null, '风萧萧兮易水寒，壮士一去兮不复还。(复还 一作：复反)探虎穴兮入蛟宫，仰天呼气兮成白虹。(此句疑为后人补作)', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('926', '关雎', null, '', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('927', '武夷山中', null, '谢枋得', null, null, '十年无梦得还家，独立青峰野水涯。天地寂寥山雨歇，几生修得到梅花？', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('928', '初夏游张园', null, '戴复古', null, null, '乳鸭池塘水浅深，熟梅天气半阴晴。东园载酒西园醉，摘尽枇杷一树金。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('929', '长歌行', null, '', null, null, '青青园中葵，朝露待日晞。阳春布德泽，万物生光辉。常恐秋节至，焜黄华叶衰。百川东到海，何时复西归？少壮不努力，老大徒伤悲！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('930', '李延年歌', null, '李延年', null, null, '北方有佳人。绝世而独立。一顾倾人城。再顾倾人国。宁不知倾城与倾国。佳人难再得。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('931', '雪梅·其一', null, '卢梅坡', null, null, '梅雪争春未肯降，骚人搁笔费评章。梅须逊雪三分白，雪却输梅一段香。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('932', '莺梭', null, '刘克庄', null, null, '掷柳迁乔太有情，交交时作弄机声。洛阳三月花如锦，多少工夫织得成。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('933', '寒夜', null, '杜耒', null, null, '寒夜客来茶当酒，竹炉汤沸火初红。寻常一样窗前月，才有梅花便不同。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('934', '观书有感二首·其一', null, '朱熹', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('935', '渡易水', null, '陈子龙', null, null, '并刀昨夜匣中鸣，燕赵悲歌最不平；易水潺湲云草碧，可怜无处送荆卿！', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('936', '短歌行', null, '曹操', null, null, '对酒当歌，人生几何！譬如朝露，去日苦多。慨当以慷，忧思难忘。何以解忧？唯有杜康。青青子衿，悠悠我心。但为君故，沉吟至今。呦呦鹿鸣，食野之苹。我有嘉宾，鼓瑟吹笙。明明如月，何时可掇？忧从中来，不可断绝。越陌度阡，枉用相存。契阔谈讌，心念旧恩。(谈讌 一作：谈宴)月明星稀，乌鹊南飞。绕树三匝，何枝可依？山不厌高，海不厌深。周公吐哺，天下归心。(海 一作：水)', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('937', '天平山中', null, '杨基', null, null, '细雨茸茸湿楝花，南风树树熟枇杷；徐行不记山深浅，一路莺啼送到家。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('938', '灵隐寺月夜', null, '厉鹗', null, null, '夜寒香界白，涧曲寺门通。月在众峰顶，泉流乱叶中。一灯群动息，孤磬四天空。归路畏逢虎，况闻岩下风。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('939', '阻雪', null, '吴伟业', null, null, '关山虽胜路难堪，才上征鞍又解骖。十丈黄尘千尺雪，可知俱不似江南。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('940', '村行', null, '王禹偁', null, null, '马穿山径菊初黄，信马悠悠野兴长。万壑有声含晚籁，数峰无语立斜阳。棠梨叶落胭脂色，荞麦花开白雪香。何事吟余忽惆怅，村桥原树似吾乡。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('941', '读山海经·其一', null, '陶渊明', null, null, '孟夏草木长，绕屋树扶疏。 众鸟欣有托，吾亦爱吾庐。 既耕亦已种，时还读我书。 穷巷隔深辙，颇回故人车。 欢言酌春酒，摘我园中蔬。 微雨从东来，好风与之俱。 泛览《周王传》，流观《山海》图。 俯仰终宇宙，不乐复何如？', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('942', '七步诗', null, '曹植', null, null, '', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('943', '山中雪后', null, '郑燮', null, null, '晨起开门雪满山，雪晴云淡日光寒。檐流未滴梅花冻，一种清孤不等闲。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('944', '过许州', null, '沈德潜', null, null, '到处陂塘决决流，垂杨百里罨平畴。行人便觉须眉绿，一路蝉声过许州。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('945', '今日诗', null, '文嘉', null, null, '今日复今日，今日何其少！今日又不为，此事何其了？人生百年几今日，今日不为真可惜！若言姑待明朝至，明朝又有明朝事。为君聊赋今日诗，努力请从今日始。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:13:09', null);
INSERT INTO `wp_poems` VALUES ('946', '麟之趾', null, '', null, null, '麟之趾，振振公子，于嗟麟兮。麟之定，振振公姓，于嗟麟兮。麟之角，振振公族，于嗟麟兮。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:29:45', null);
