INSERT INTO `wp_poems` VALUES ('1052', '定之方中', null, '', null, null, '定之方中，作于楚宫。揆之以日，作于楚室。树之榛栗，椅桐梓漆，爰伐琴瑟。升彼虚矣，以望楚矣。望楚与堂，景山与京。降观于桑。卜云其吉，终焉允臧。灵雨既零，命彼倌人。星言夙驾，说于桑田。匪直也人，秉心塞渊。騋牝三千。', null, null, null, null, null, null, null, null, null, null, null, null, null, null, '2017-01-05 01:29:46', null);
